mapper.update:

	UPDATE product 
	SET cate_no = #{cateNo}, title = #{title}, info = #{info}, price = #{price}, img1 = #{img1}, deli_price = #{deliPrice}, stock = #{stock}, min_count = #{minCount}, max_count = #{maxCount}, state = #{state}, id = #{id}, upt_date = now(), sort = #{sort} 
	WHERE no = #{no}
